    enable(): void {
        this.isPaused = false
        this.setFlushTimeout()
    }

    private setFlushTimeout(): void {
        if (this.isPaused) {
            return
        }
        this.flushTimeout = setTimeout(() => {
            this.clearFlushTimeout()
            if (this.queue.length > 0) {
                const requests = this.formatQueue()
